import React from 'react';
import './TreeItem.css';

class TreeItem extends React.Component {
    getsizedesc=function(totalsize){
        if (totalsize < 1024) {
            return ""+ totalsize+"B"
        }
    
        if (totalsize < 1024*1024) {
            return ""+(totalsize / 1024).toFixed(2)+ "KB"
        }
    
        if (totalsize < 1024*1024*1024) {
            return ""+(totalsize / 1024/ 1024).toFixed(2)+ "MB"
        }
    
        if (totalsize < 1024*1024*1024*1024) {
            return  ""+(totalsize / 1024/ 1024/ 1024).toFixed(2)+ "GB"
        }
        return ""+totalsize
    }
    render() {
        return (
            <div className="size-gb" >
                <div className="itemname">{this.props.Name}</div>
                <div className="itemsize">{this.getsizedesc(this.props.TotalSize)}</div>
            </div >
        )
    }
}

export default TreeItem;
