import { Tree } from 'antd';
import React from 'react';

const DirectoryTree = Tree.DirectoryTree;
const { TreeNode } = Tree;

class DirTree extends React.Component {
    constructor(props) {
        super(props);
        var url = 'http://localhost:9898/dir?root=D:\\Tools';
        fetch(url,{mode: 'cors'}).then(res=> {
            console.log('res'+res);
            var t=this;
            res.json().then(function (data) {
                console.log(data);
                t.setState({
                    dirinfo: data
                })
            });
        })

    }


    onSelect = (keys, event) => {
        console.log('Trigger Select', keys, event);
    };

    onExpand = () => {
        console.log('Trigger Expand');
    };



    render() {
        if(this.state==null){
            return (<div></div>);
        }
        let content =  this.state.dirinfo.SubFiles.map((item, index) => {
            return (<TreeNode title={item.name} key="0-0">
            </TreeNode>)
        });
        return (
            <DirectoryTree
                multiple
                defaultExpandAll
                onSelect={this.onSelect}
                onExpand={this.onExpand}
            >
                {content}
            </DirectoryTree>
        );
    }
}

export default DirTree;
