import { Tree } from 'antd';
import React from 'react';

const DirectoryTree = Tree.DirectoryTree;
const { TreeNode } = Tree;

class DirTree extends React.Component {
    constructor(props) {
        super(props);
        var url = 'http://localhost:9898/dir?root=D:\\Tools';
        fetch(url, { mode: 'cors' }).then(res => {
            console.log('res' + res);
            var t = this;
            res.json().then(function (data) {
                console.log(data);
                t.setState({
                    dirinfo: data
                })
            });
        })

    }


    onSelect = (keys, event) => {
        console.log('Trigger Select', keys, event);
    };

    onExpand = () => {
        console.log('Trigger Expand');
    };

    getsizedesc=function(totalsize){
        if (totalsize < 1024) {
            return ""+ totalsize+"B"
        }
    
        if (totalsize < 1024*1024) {
            return ""+(totalsize / 1024/ 1024).toFixed(2)+ "MB"
        }
    
        if (totalsize < 1024*1024*1024) {
            return ""+(totalsize / 1024/ 1024).toFixed(2)+ "MB"
        }
    
        if (totalsize < 1024*1024*1024*1024) {
            return  ""+(totalsize / 1024/ 1024/ 1024).toFixed(2)+ "GB"
        }
        return ""+totalsize
    }

    rendersubfiles = function (subfile) {
        if (subfile.SubFiles == null) {
            return (<TreeNode title={subfile.Name+"     "+this.getsizedesc(subfile.TotalSize)} key={subfile.Name} isLeaf />);
        } else {
            let subfilecontent = subfile.SubFiles.map((item, index) => {
               return(this.rendersubfiles(item));
            });
            return (<TreeNode title={subfile.Name+"     "+this.getsizedesc(subfile.TotalSize)} key={subfile.Name}>
                {subfilecontent}
            </TreeNode>)
        }
    }



    render() {
        if (this.state == null) {
            return (<div></div>);
        }
        let content = this.rendersubfiles(this.state.dirinfo)
        return (
            <DirectoryTree
                multiple
                defaultExpandAll
                onSelect={this.onSelect}
                onExpand={this.onExpand}
            >
                {content}
            </DirectoryTree>
        );
    }
}

export default DirTree;
