import React from 'react';
import './TreeItem.css';

class TreeItem extends React.Component {
    getsizedesc=function(totalsize){
        if (totalsize < 1024) {
            return ""+ totalsize+"B"
        }
    
        if (totalsize < 1024*1024) {
            return ""+(totalsize / 1024).toFixed(2)+ "KB"
        }
    
        if (totalsize < 1024*1024*1024) {
            return ""+(totalsize / 1024/ 1024).toFixed(2)+ "MB"
        }
    
        if (totalsize < 1024*1024*1024*1024) {
            return  ""+(totalsize / 1024/ 1024/ 1024).toFixed(2)+ "GB"
        }
        return ""+totalsize
    }
    render() {
        let content=undefined;
        var totalsize=this.props.TotalSize;
        if (totalsize < 1024) {
            content= <div className="treeitem size-gb" >
            <div className="itemname">{this.props.Name}</div>
            <div className="itemsize">{""+ totalsize+"B"}</div>
        </div >
        }
    
        if (totalsize < 1024*1024) {
            content= <div className="treeitem size-gb" >
            <div className="itemname">{this.props.Name}</div>
            <div className="itemsize">{""+(totalsize / 1024).toFixed(2)+ "KB"}</div>
        </div >
        }
    
        if (totalsize < 1024*1024*1024) {
            content= <div className="treeitem size-gb" >
            <div className="itemname">{this.props.Name}</div>
            <div className="itemsize">{""+(totalsize / 1024/ 1024).toFixed(2)+ "MB"}</div>
        </div >
        }
    
        if (totalsize < 1024*1024*1024*1024) {
            content= <div className="treeitem size-gb" >
            <div className="itemname">{this.props.Name}</div>
            <div className="itemsize">{""+(totalsize / 1024/ 1024/ 1024).toFixed(2)+ "GB"}</div>
        </div >
        }
        return ""+totalsize
        return (
            <div className="treeitem size-gb" >
                <div className="itemname">{this.props.Name}</div>
                <div className="itemsize">{this.getsizedesc(this.props.TotalSize)}</div>
            </div >
        )
    }
}

export default TreeItem;
